mapper.select:
select

from t_goods_group
where 1 = 1

	and goodsId = #{goodsid,jdbcType=VARCHAR}


	and groupNo = #{groupno,jdbcType=VARCHAR}


	and openFlg = #{openflg,jdbcType=CHAR}

select A.groupNo as groupId, A.goodsId as goodsId, A.groupPrice as goodsPrice, A.groupMaxQuantity as goodsMax, A.groupCurrentQuantity as goodsCurr, A.openFlg as isOpen, A.topPageUp as isTopUp, A.preFlg as isPre, A.inStockFlg as isInStock, A.hotFlg as isHot, A.diamondShowFlg as isDiamond, A.enShowFlg as isEn, A.sortOrder as sortOrder, DATE_FORMAT(A.validPeriodStart,'%Y/%m/%d %T') as validDateFrom, DATE_FORMAT(A.validPeriodEnd,'%Y/%m/%d %T') as validDateTo, B.goodsName as goodsName, C.priceValue as cost from t_goods_group A inner join t_goods B on A.goodsId = B.goodsId inner join t_goods_price C on A.goodsId = C.goodsId WHERE A.openFlg <> '9'
and B.goodsName like concat('%',#{goodsName,jdbcType=VARCHAR},'%')
and A.goodsId = #{goodsId,jdbcType=VARCHAR}
and A.openFlg = #{isOpenFlag,jdbcType=VARCHAR}
and A.topPageUp = #{isTopUp,jdbcType=VARCHAR}
and A.preFlg = #{isPre,jdbcType=VARCHAR}
and A.inStockFlg = #{isInStock,jdbcType=VARCHAR}
and A.hotFlg = #{isHot,jdbcType=VARCHAR}
and A.diamondShowFlg = #{isDiamond,jdbcType=VARCHAR}
and A.enShowFlg = #{isEn,jdbcType=VARCHAR}
and (DATE_FORMAT(A.validPeriodStart,'%Y/%m/%d')) >= #{dateFrom,jdbcType=VARCHAR}
and (DATE_FORMAT(A.validPeriodEnd,'%Y/%m/%d')) <= #{dateTo,jdbcType=VARCHAR}
order by A.updTimestamp desc

		SELECT count(1)
		from t_goods_group A
	    inner join t_goods B
	    on A.goodsId = B.goodsId
	    inner join t_goods_price C
	    on A.goodsId = C.goodsId
		WHERE A.openFlg  <>  '9'

			and B.goodsName like concat('%',#{goodsName,jdbcType=VARCHAR},'%')


        	and A.goodsId = #{goodsId,jdbcType=VARCHAR}


        	and A.openFlg = #{isOpenFlag,jdbcType=VARCHAR}


        	and A.topPageUp = #{isTopUp,jdbcType=VARCHAR}


        	and A.preFlg = #{isPre,jdbcType=VARCHAR}


        	and A.inStockFlg = #{isInStock,jdbcType=VARCHAR}


        	and A.hotFlg = #{isHot,jdbcType=VARCHAR}


        	and A.diamondShowFlg = #{isDiamond,jdbcType=VARCHAR}


        	and A.enShowFlg = #{isEn,jdbcType=VARCHAR}


        	and (DATE_FORMAT(A.validPeriodStart,'%Y/%m/%d')) >= #{dateFrom,jdbcType=VARCHAR}


        	and (DATE_FORMAT(A.validPeriodEnd,'%Y/%m/%d')) <= #{dateTo,jdbcType=VARCHAR}

select a.orderNo, b.goodsId, c.nickName as customerName, a.quantity, a.handleFlg, DATE_FORMAT(e.orderTimestamp,'%Y/%m/%d') as orderDate, d.telNo as customerPhone from t_cons_order_details a inner join t_goods b on a.goodsId = b.goodsId inner join t_customer_basic_info c on a.customerNo = c.customerNo inner join t_customer_security_info d on a.customerNo = d.customerNo left join t_cons_order e on a.orderNo = e.orderNo where 1=1
and b.goodsName like concat('%',#{goodsName,jdbcType=VARCHAR},'%')
and (DATE_FORMAT(e.orderTimestamp,'%Y/%m/%d')) >= #{dateFrom,jdbcType=VARCHAR}
and (DATE_FORMAT(e.orderTimestamp,'%Y/%m/%d')) <= #{dateTo,jdbcType=VARCHAR}
and a.handleFlg = #{handleFlg,jdbcType=VARCHAR}

select count(1) from t_cons_order_details a inner join t_goods b on a.goodsId = b.goodsId inner join t_customer_basic_info c on a.customerNo = c.customerNo inner join t_customer_security_info d on a.customerNo = d.customerNo left join t_cons_order e on a.orderNo = e.orderNo where 1=1
and b.goodsName like concat('%',#{goodsName,jdbcType=VARCHAR},'%')
and (DATE_FORMAT(e.orderTimestamp,'%Y/%m/%d')) >= #{dateFrom,jdbcType=VARCHAR}
and (DATE_FORMAT(e.orderTimestamp,'%Y/%m/%d')) <= #{dateTo,jdbcType=VARCHAR}
and a.handleFlg = #{handleFlg,jdbcType=VARCHAR}
