mapper.select:
select

from t_goods
where 1=1

	and goodsId = #{goodsid,jdbcType=VARCHAR}


	and classId = #{classid,jdbcType=VARCHAR}


	and goodsName = #{goodsname,jdbcType=VARCHAR}


	and goodsDesc = #{goodsdesc,jdbcType=VARCHAR}


	and goodsComments = #{goodscomments,jdbcType=VARCHAR}


	and goodsThumbnail = #{goodsthumbnail,jdbcType=VARCHAR}


	and goodsSmallPic = #{goodssmallpic,jdbcType=VARCHAR}


	and goodsNormalPic = #{goodsnormalpic,jdbcType=VARCHAR}


	and onSaleFlg = #{onsaleflg,jdbcType=CHAR}


	and hotSaleFlg = #{hotsaleflg,jdbcType=CHAR}


	and newSaleFlg = #{newsaleflg,jdbcType=CHAR}


	and costPrice = #{costprice,jdbcType=DECIMAL}


	and sortOrder = #{sortorder,jdbcType=INTEGER}


	and tabs = #{tabs,jdbcType=VARCHAR}


	and deleteFlg = #{deleteflg,jdbcType=CHAR}

select a.no, a.goodsId, a.classId, a.goodsBrand, a.goodsName, a.goodsDesc, a.goodsComments, a.goodsThumbnail, a.goodsSmallPic, a.goodsNormalPic, a.onSaleFlg, a.hotSaleFlg, a.newSaleFlg, c.groupPrice as costPrice, a.sortOrder, a.tabs, a.deleteFlg, c.groupNo from t_goods_group c inner join t_goods_price b on c.goodsId = b.goodsId and (b.pricePolicy = '0' and b.openFlg = '0') inner join t_goods a on c.goodsId = a.goodsId where a.onSaleFlg= '1' and (c.openFlg = '1')
and classId = #{classId,jdbcType=VARCHAR}
limit ${limitParam}

select a.no, a.goodsId, a.classId, a.goodsBrand, a.goodsName, a.goodsDesc, a.goodsComments, a.goodsThumbnail, a.goodsSmallPic, a.goodsNormalPic, a.onSaleFlg, a.hotSaleFlg, a.newSaleFlg, c.groupPrice as disPrice, b.priceValue as costPrice, a.sortOrder, a.tabs, a.deleteFlg, c.validPeriodStart as validStartTime, c.validPeriodEnd as validEndTime, c.groupMaxQuantity as groupMax, IF(c.groupCurrentQuantity > c.groupMaxQuantity,c.groupMaxQuantity,c.groupCurrentQuantity) as groupCurrent, c.preFlg as preLabel, c.inStockFlg as inStockLabel, c.hotFlg as hotLabel, c.topPageUp as salesLabel, c.diamondShowFlg as diamondLabel, c.sellOutInitQuantity as sellOutInitQuantity, c.sellOutFlg as sellOutFlg, c.groupQuantityLimit as groupQuantityLimit, c.groupNo from t_goods_group c inner join t_goods_price b on c.goodsId = b.goodsId and (b.pricePolicy = '0' and b.openFlg = '0') inner join t_goods a on c.goodsId = a.goodsId where a.onSaleFlg= '1' and (c.openFlg = '1') and c.groupCurrentQuantity < c.groupMaxQuantity and c.validPeriodEnd > NOW()
and a.classId = #{classId,jdbcType=VARCHAR}
and a.hotSaleFlg = #{hotSaleFlg,jdbcType=VARCHAR}
and c.topPageUp = #{newSaleFlg,jdbcType=VARCHAR}
and c.enShowFlg = #{enShowFlg,jdbcType=VARCHAR}
and a.goodsName like concat('%',#{goodsName},'%')
and a.goodsBrand = #{brand,jdbcType=VARCHAR}
and DATEDIFF(c.validPeriodEnd,NOW()) <= #{daySearch}
and c.topPageUp = #{topPageUp,jdbcType=VARCHAR}
and c.preFlg = #{preFlg,jdbcType=VARCHAR}
and c.inStockFlg = #{inStockFlg,jdbcType=VARCHAR}
and c.diamondShowFlg = #{diamondShowFlg,jdbcType=VARCHAR}
and c.hotFlg = #{hotFlg,jdbcType=VARCHAR}
order by c.sortOrder desc,
c.groupCurrentQuantity desc,
c.groupNo desc,
c.groupPrice,
a.classId,
c.addTimestamp desc

SELECT count(1)
from t_goods_group c 
inner join t_goods_price b on c.goodsId = b.goodsId
and (b.pricePolicy = '0' and b.openFlg = '0')
inner join t_goods a on c.goodsId = a.goodsId
where a.onSaleFlg= '1'
and (c.openFlg = '1')
and c.groupCurrentQuantity  <  c.groupMaxQuantity
and c.validPeriodEnd  >   NOW()

	and a.classId = #{classId,jdbcType=VARCHAR}


	and a.hotSaleFlg = #{hotSaleFlg,jdbcType=VARCHAR}


	and c.topPageUp = #{newSaleFlg,jdbcType=VARCHAR}


	and c.enShowFlg = #{enShowFlg,jdbcType=VARCHAR}


	and a.goodsName like concat('%',#{goodsName},'%')


	and a.goodsBrand = #{brand,jdbcType=VARCHAR}


	and DATEDIFF(c.validPeriodEnd,NOW())  <=  #{daySearch}


	and c.topPageUp = #{topPageUp,jdbcType=VARCHAR}


	and c.preFlg = #{preFlg,jdbcType=VARCHAR}


	and c.inStockFlg = #{inStockFlg,jdbcType=VARCHAR}


	and c.diamondShowFlg = #{diamondShowFlg,jdbcType=VARCHAR}


	and c.hotFlg = #{hotFlg,jdbcType=VARCHAR}

select a.no, a.goodsId, a.classId, a.goodsBrand, a.goodsName, a.goodsDesc, a.goodsComments, a.goodsThumbnail, a.goodsSmallPic, a.goodsNormalPic, a.onSaleFlg, a.hotSaleFlg, a.newSaleFlg, c.groupPrice as disPrice, b.priceValue as costPrice, a.sortOrder, a.tabs, a.deleteFlg, c.validPeriodEnd as validEndTime, c.groupMaxQuantity as groupMax, IF(c.groupCurrentQuantity > c.groupMaxQuantity,c.groupMaxQuantity,c.groupCurrentQuantity) as groupCurrent, c.preFlg as preLabel, c.inStockFlg as inStockLabel, c.hotFlg as hotLabel, c.topPageUp as salesLabel, c.groupNo from t_goods_group c inner join t_goods_price b on c.goodsId = b.goodsId and (b.pricePolicy = '0' and b.openFlg = '0') inner join t_goods a on c.goodsId = a.goodsId inner join t_tab_index f on f.goodsId = c.goodsId where a.onSaleFlg= '1' and (c.openFlg = '1')
and f.tabId = #{tabId,jdbcType=VARCHAR}
order by c.validPeriodEnd, c.groupNo

select
a.no, a.goodsId, a.classId, a.goodsBrand, a.goodsName, a.goodsDesc, a.goodsComments,
a.goodsThumbnail,
a.goodsSmallPic, a.goodsNormalPic, a.onSaleFlg, a.hotSaleFlg, a.newSaleFlg, 
c.groupPrice as  costPrice, 
a.sortOrder, a.tabs,
a.deleteFlg, 
c.groupNo
from t_goods_group c 
inner join t_goods_price b on c.goodsId = b.goodsId
and (b.pricePolicy = '0' and b.openFlg = '0')
inner join t_goods a on c.goodsId = a.goodsId
where a.onSaleFlg= '1'
and (and c.openFlg = '1')

	and goodsId = #{goodsid,jdbcType=VARCHAR}


	and classId = #{classid,jdbcType=VARCHAR}


	and goodsName = #{goodsname,jdbcType=VARCHAR}


	and goodsDesc = #{goodsdesc,jdbcType=VARCHAR}


	and goodsComments = #{goodscomments,jdbcType=VARCHAR}


	and goodsThumbnail = #{goodsthumbnail,jdbcType=VARCHAR}


	and goodsSmallPic = #{goodssmallpic,jdbcType=VARCHAR}


	and goodsNormalPic = #{goodsnormalpic,jdbcType=VARCHAR}


	and onSaleFlg = #{onsaleflg,jdbcType=CHAR}


	and hotSaleFlg = #{hotsaleflg,jdbcType=CHAR}


	and newSaleFlg = #{newsaleflg,jdbcType=CHAR}


	and costPrice = #{costprice,jdbcType=DECIMAL}


	and sortOrder = #{sortorder,jdbcType=INTEGER}


	and tabs = #{tabs,jdbcType=VARCHAR}


	and deleteFlg = #{deleteflg,jdbcType=CHAR}

limit 5

select
a.no, a.goodsId, a.classId, a.goodsBrand, a.goodsName, a.goodsDesc, a.goodsComments,
a.goodsThumbnail,
a.goodsSmallPic, a.goodsNormalPic, a.onSaleFlg, a.hotSaleFlg, a.newSaleFlg, 
c.groupPrice as  costPrice, 
a.sortOrder, a.tabs,
a.deleteFlg, 
c.groupNo
from t_goods_group c 
inner join t_goods_price b on c.goodsId = b.goodsId
and (b.pricePolicy = '0' and b.openFlg = '0')
inner join t_goods a on c.goodsId = a.goodsId
where a.onSaleFlg= '1'
and (c.openFlg = '1')

	and goodsId = #{goodsid,jdbcType=VARCHAR}


	and classId = #{classid,jdbcType=VARCHAR}


	and goodsName = #{goodsname,jdbcType=VARCHAR}


	and goodsDesc = #{goodsdesc,jdbcType=VARCHAR}


	and goodsComments = #{goodscomments,jdbcType=VARCHAR}


	and goodsThumbnail = #{goodsthumbnail,jdbcType=VARCHAR}


	and goodsSmallPic = #{goodssmallpic,jdbcType=VARCHAR}


	and goodsNormalPic = #{goodsnormalpic,jdbcType=VARCHAR}


	and onSaleFlg = #{onsaleflg,jdbcType=CHAR}


	and hotSaleFlg = #{hotsaleflg,jdbcType=CHAR}


	and newSaleFlg = #{newsaleflg,jdbcType=CHAR}


	and costPrice = #{costprice,jdbcType=DECIMAL}


	and sortOrder = #{sortorder,jdbcType=INTEGER}


	and tabs = #{tabs,jdbcType=VARCHAR}


	and deleteFlg = #{deleteflg,jdbcType=CHAR}

select A.goodsId as goodsId, A.goodsName as goodsName, A.goodsThumbnail as goodsPic, A.costPrice as goodsCost, A.onSaleFlg as onsale, A.hotSaleFlg as hotsale, A.newSaleFlg as newsale, (case when B.priceValue is null then '0' else '1' end) as isSetPrice from t_goods A left join t_goods_price B on A.goodsId = B.goodsId WHERE A.deleteFlg <> '1'
and A.goodsName like concat('%',#{goodsName,jdbcType=VARCHAR},'%')
and A.hotSaleFlg = #{isHotSale,jdbcType=VARCHAR}
and A.newSaleFlg = #{isNewSale,jdbcType=VARCHAR}
and A.classId = #{goodsClass,jdbcType=VARCHAR}
and B.priceValue is not null

	SELECT count(1)
	FROM t_goods A 
	left join t_goods_price B
    on A.goodsId = B.goodsId
	WHERE A.deleteFlg  <> '1' 

		and A.goodsName like concat('%',#{goodsName,jdbcType=VARCHAR},'%')


		and A.hotSaleFlg = #{isHotSale,jdbcType=VARCHAR}


		and A.newSaleFlg = #{isNewSale,jdbcType=VARCHAR}


		and A.classId = #{goodsClass,jdbcType=VARCHAR}


		and B.priceValue is not null

select goodsId, classId, goodsName, goodsDesc, onSaleFlg, hotSaleFlg, newSaleFlg, costPrice from t_goods WHERE deleteFlg <> '1'
and goodsName like concat('%',#{goodsName,jdbcType=VARCHAR},'%')
and hotSaleFlg = #{isHotSale,jdbcType=VARCHAR}
and newSaleFlg = #{isNewSale,jdbcType=VARCHAR}
and classId = #{goodsClass,jdbcType=VARCHAR}

SELECT count(1)
FROM t_goods
WHERE deleteFlg  <> '1' 

	and goodsName like concat('%',#{goodsName,jdbcType=VARCHAR},'%')


	and hotSaleFlg = #{isHotSale,jdbcType=VARCHAR}


	and newSaleFlg = #{isNewSale,jdbcType=VARCHAR}


	and classId = #{goodsClass,jdbcType=VARCHAR}
