mapper.select:
select
from t_powder_order where 1=1
and ordreNo = #{ordreNo,jdbcType=VARCHAR}

	select 

  from t_powder_order
  where 1=1

      and customerId = #{customerId,jdbcType=VARCHAR}


      and paymentStatus = #{paymentStatus,jdbcType=CHAR}


      and status = #{status,jdbcType=CHAR}

select id as orderId, ordreNo as orderNo, status, orderDate, sumAmount as totalAmount, paymentMethod as paymentMethod from t_powder_order where 1=1
and customerId = #{customerId,jdbcType=VARCHAR}
and status = #{status,jdbcType=CHAR}

	select 
  count(1)
  from t_powder_order
  where 1=1

      and customerId = #{customerId,jdbcType=VARCHAR}


      and status = #{status,jdbcType=CHAR}
